import { Component } from 'react';
import { Link , Routes, Route, useLocation, useNavigate} from 'react-router-dom';
import HomePage from '../srcCopy/pages/HomePage';
import Book from '../srcCopy/pages/Book';
import Bookcase from '../srcCopy/pages/Bookcase';
import Login from '../srcCopy/pages/Login';
import { EthProvider } from "./contexts/EthContext";
import Demo from "./components/Demo";
import "./App.css";

function App() {
  return (
    <EthProvider>
      <div id="App" >
        <div className="container">
          <Demo />
        </div>
        <header className={styles.header}>
          <div className = {styles.box}>
            <Link to="/"><div className={styles.boxleft}>bookChain</div></Link>
          </div>
          <div className = {styles.boxright}>
            <Link to="/login" className={styles.rightButton}>log in</Link>
            <Link to="/bookcase" className ={styles.rightButton}>bookcase</Link>
          </div>
        </header>
        <section className = {styles.content}>
          <Routes>
              <Route path="/" element={<HomePage/>} />
              <Route path="/login" element={<Login/>} />
              <Route path="/bookcase" element={<Bookcase/>}/>
              <Route path="/books/:id" element={<Book/>}/>
          </Routes>
        </section>
        <footer className = {styles.footer}>
            <h3>
            信箱：jjjcd@gmail.com
            </h3> 
        <div>
          <a href="https://fortunenina.pixnet.net/blog/post/113760749" target="_blank">更多訊息</a>
        </div>
        </footer>
      </div>
    </EthProvider>
  );
}

export default App;
