import Web3 from 'web3';
import React, { Component } from 'react';
var Web = require('web3');
var web3 = new Web();
const ethEnabled = () => {
  if (window.ethereum) {
    window.web3 = new Web3(window.ethereum);
    window.ethereum.enable();
    return true;
  }
  return false;
}
ethEnabled();


class Account extends Component {

  constructor() {
    super();
    this.state = {
      accounts: ''
    }
  }

  componentWillMount() {
    console.log(web3);
    web3.setProvider(new web3.providers.HttpProvider('http://localhost:7545')); //指定為RPC server的位置
    web3.eth.getAccounts(function(err, result){
        setState({accounts: result});
      });
    console.log(web3.eth.accounts)
  }

  render() {
    return (
      <div>
        <p>
          {this.state.accounts.map((i, idx) => (
            <p>帳號{idx}: {i} </p>
          ))}
        </p>
      </div>
    );
  }
}

export default Account;
